import React, { useState, useEffect } from 'react';
import data from '../data';
import {
  GoogleMap,
  Marker,
  InfoWindow,
  useJsApiLoader,
} from '@react-google-maps/api';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const containerStyle = {
  display: 'flex',
  width: '600px',
  height: '500px',
};

const center = {
  lat: 45.66667,
  lng: 21.5,
};

function MyComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: 'AIzaSyBqSxES0gUd7JoNggJUjnRh5bDS2JXjUZ4',
  });

  const navigate = useNavigate();
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);

  const [shelters, setShelters] = useState([]);

  useEffect(() => {
    const fetchShelters = async () => {
      try {
        const response = await axios.get('/api/shelters');
        setShelters(response.data);
        console.log(shelters);
      } catch (error) {
        console.error('Error fetching shelters:', error);
      }
    };

    fetchShelters();
  }, []);

  useEffect(() => {
    if (isLoaded) {
      const geocoder = new window.google.maps.Geocoder();
      const newMarkers = [];

      shelters.forEach((shelter, index) => {
        const address = shelter.address;
        geocoder.geocode({ address: address }, (results, status) => {
          if (status === 'OK') {
            const location = results[0].geometry.location;
            newMarkers.push({
              position: { lat: location.lat(), lng: location.lng() },
              title: shelter.name,
              id: index,
            });
            setMarkers([...newMarkers]);
          } else {
            console.error(
              `Geocode was not successful for the following reason: ${status}`
            );
          }
        });
      });
    }
  }, [isLoaded, shelters]);

  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);

    setMap(map);
  }, []);

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null);
  }, []);

  const handleMarkerClick = (shelterName) => {
    //setSelectedMarker(marker);
    navigate(`/shelter/${encodeURIComponent(shelterName)}`);
  };

  const handleInfoWindowClose = () => {
    setSelectedMarker(null);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={10}
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      {markers.map((marker, index) => (
        <Marker
          position={marker.position}
          onClick={() => handleMarkerClick(marker.title)} // Folosește titlul marker-ului (care este numele adăpostului) pentru a transmite numele la apelul funcției handleMarkerClick
        >
          {/* Restul codului pentru marker */}
        </Marker>
      ))}
    </GoogleMap>
  ) : (
    <></>
  );
}

export default React.memo(MyComponent);
