import React, { useContext, useEffect, useReducer } from 'react';
import axios from 'axios';
import { Link, useLocation, useNavigate, useParams } from 'react-router-dom';
import { toast } from 'react-toastify';
import { Store } from '../store';
import { getError } from '../utils';
import MessageBox from '../components/MessageBox';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return {
        ...state,
        pets: action.payload,
        loading: false,
      };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };

    case 'DELETE_REQUEST':
      return { ...state, loadingDelete: true, successDelete: false };
    case 'DELETE_SUCCESS':
      return {
        ...state,
        loadingDelete: false,
        successDelete: true,
      };
    case 'DELETE_FAIL':
      return { ...state, loadingDelete: false, successDelete: false };
    case 'DELETE_RESET':
      return { ...state, loadingDelete: false, successDelete: false };
    default:
      return state;
  }
};

export default function ProfileScreen() {
  const [{ loading, error, pets, loadingDelete, successDelete }, dispatch] =
    useReducer(reducer, {
      pets: [],
      loading: true,
      error: '',
    });
  const navigate = useNavigate();
  const { search } = useLocation();

  const { state } = useContext(Store);
  const { userInfo } = state;
  const params = useParams();
  const { name } = params;

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get(`/api/pets/user/${name}`, {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });

        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
      }
    };
    if (successDelete) {
      dispatch({ type: 'DELETE_RESET' });
      fetchData(); // Re-fetch data after successful delete
    } else {
      fetchData();
    }
  }, [userInfo, successDelete, name]);

  const deleteHandler = async (petId) => {
    if (window.confirm('Are you sure to delete?')) {
      try {
        dispatch({ type: 'DELETE_REQUEST' });
        await axios.delete(`/api/pets/${petId}`, {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });
        toast.success('Pet deleted successfully');
        dispatch({ type: 'DELETE_SUCCESS' });
      } catch (err) {
        toast.error(getError(err));
        dispatch({
          type: 'DELETE_FAIL',
        });
      }
    }
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <>
          <h2>My Posts</h2>
          {pets.map((pet) => (
            <div key={pet._id} className="mb-3">
              <h3>{pet.name}</h3>
              <p>{pet.description}</p>

              <button
                className="btn btn-danger"
                disabled={loadingDelete}
                onClick={() => deleteHandler(pet._id)}
              >
                {loadingDelete ? 'Deleting...' : 'Delete'}
              </button>
            </div>
          ))}
        </>
      )}
    </div>
  );
}
