import './App.css';
import { BrowserRouter, Link, Route, Routes } from 'react-router-dom';
import HomeScreen from './screens/HomeScreen';
import ProductScreen from './screens/PetScreen';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Badge from 'react-bootstrap/Badge';
import Container from 'react-bootstrap/Container';
import { LinkContainer } from 'react-router-bootstrap';
import PetScreen from './screens/PetScreen';
import { useContext } from 'react';
import { Store } from './store';
import FavoritesScreen from './screens/FavoritesScreen';
import SigninScreen from './screens/SignInScreen';

function App() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { favorites, userInfo } = state;

  const signoutHandler = () => {
    ctxDispatch({ type: 'USER_SIGNOUT' });
    localStorage.removeItem('userInfo');
  };

  return (
    <BrowserRouter>
      <div className="d-flex flex-column site-container">
        <header>
          <Navbar className="navbar" variant="dark">
            <Container>
              <LinkContainer to="/">
                <Navbar.Brand>Pet Adopt</Navbar.Brand>
              </LinkContainer>
              <Nav className="me-auto">
                <Link to="/favorites" className="nav-link">
                  <i className="fas fa-heart"></i>
                  {favorites.favoritesItems.length > 0 && (
                    <Badge pill bg="danger">
                      {favorites.favoritesItems.length}
                    </Badge>
                  )}
                </Link>
                {userInfo ? (
                  <NavDropdown title={userInfo.name} id="basic-nav-dropdown">
                    <LinkContainer to="/profile">
                      <NavDropdown.Item>Profil</NavDropdown.Item>
                    </LinkContainer>
                    <NavDropdown.Divider />
                    <Link
                      className="dropdown-item"
                      to="#signout"
                      onClick={signoutHandler}
                    >
                      Iesi din cont
                    </Link>
                  </NavDropdown>
                ) : (
                  <Link className="nav-link" to="/signin">
                    Autentificare
                  </Link>
                )}
              </Nav>
            </Container>
          </Navbar>
        </header>
        <main>
          <Container className="mt-3">
            <Routes>
              <Route path="/pet/:slug" element={<PetScreen />} />
              <Route path="/favorites" element={<FavoritesScreen />} />
              <Route path="/" element={<HomeScreen />} />
              <Route path="/signin" element={<SigninScreen />} />
            </Routes>
          </Container>
        </main>
        <footer>
          <div className="text-center">All rights reserved</div>
        </footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
