import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'incoming', text: 'Hi there ðŸ‘‹\nHow can I help you today?' },
  ]);
  const [newMessage, setNewMessage] = useState('');
  const [questionId, setQuestionId] = useState('');

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSendMessage = async () => {
    // Implementarea trimiterii Ã®ntrebÄƒrii È™i preluarea ID-ului Ã®ntrebÄƒrii
    try {
      const response = await axios.post('/api/chatbot/questions', {
        text: newMessage,
        category: 'default',
      });
      const { _id } = response.data;
      setQuestionId(_id);
    } catch (error) {
      console.error('Error:', error);
      // TrateazÄƒ eroarea
    }
    setNewMessage('');
  };

  useEffect(() => {
    if (questionId) {
      // ObÈ›ine rÄƒspunsurile pentru Ã®ntrebarea specificatÄƒ
      const getAnswers = async () => {
        try {
          const response = await axios.get(
            `/api/chatbot/questions/${questionId}/answers`
          );
          const answers = response.data;
          // ActualizeazÄƒ mesajele cu rÄƒspunsurile obÈ›inute
          setMessages([
            ...messages,
            ...answers.map((answer) => ({
              type: 'incoming',
              text: answer.text,
            })),
          ]);
        } catch (error) {
          console.error('Error:', error);
          // TrateazÄƒ eroarea
        }
      };
      getAnswers();
    }
  }, [questionId, messages]);

  return (
    <>
      <button className="chatbot-toggler" onClick={handleToggle}>
        <div className="material-symbols-rounded">
          <i className="fas fa-comment"></i>
        </div>
        <span className="material-symbols-outlined">close</span>
      </button>
      {isOpen && (
        <div className="chatbot">
          <header>
            <h2>Chatbot</h2>
            <span
              className="close-btn material-symbols-outlined"
              onClick={handleToggle}
            >
              close
            </span>
          </header>
          <ul className="chatbox">
            {messages.map((message, index) => (
              <li key={index} className={`chat ${message.type}`}>
                {message.type === 'incoming' && (
                  <span className="material-symbols-outlined">smart_toy</span>
                )}
                <p>{message.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Enter a response..."
              spellCheck="false"
              required
              value={newAnswer}
              onChange={(e) => setNewAnswer(e.target.value)}
            />
            <input
              type="text"
              placeholder="Question ID"
              value={questionId}
              onChange={(e) => setQuestionId(e.target.value)}
            />
            <span
              id="send-btn"
              className="material-symbols-rounded"
              onClick={handleSendMessage}
            >
              send
            </span>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
