import { Link } from 'react-router-dom';
//import data from '../data';
import axios from 'axios';
import { useEffect, useReducer, useState } from 'react';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, pets: action.payload, loading: false };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function HomeScreen() {
  const [{ loading, error, pets }, dispatch] = useReducer(reducer, {
    pets: [],
    loading: true,
    error: '',
  });
  //const [pets, setPets] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get('/api/pets');
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
      }
    };
    fetchData();
  }, []);
  return (
    <div>
      <h1>Animale disponibile</h1>
      <div className="pets">
        {loading ? (
          <div>Loading...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          <Row>
            pets.map((pet) => (
            <div className="pet" key={pet.slug}>
              <Link to={`/pet/${pet.slug}`}>
                <img src={pet.photos} alt={pet.name} />
              </Link>
              <div className="pet-info">
                <Link className="name-link" to={`/pet/${pet.slug}`}>
                  <p>
                    <strong>{pet.name}</strong>
                  </p>
                </Link>
                <p>{pet.address}</p>
                <div className="pet-tags">
                  <p>{pet.age} ani</p>
                  <p>{pet.gender}</p>
                </div>
                <p>{pet.description}</p>
              </div>
            </div>
            ))
          </Row>
        )}
      </div>
    </div>
  );
}
export default HomeScreen;
