import React, { useState } from 'react';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'incoming', text: 'Hi there ðŸ‘‹\nHow can I help you today?' },
  ]);
  const [newMessage, setNewMessage] = useState('');
  const API_KEY = 'sk-proj-52u1RgKbVsWfl6L5hDReT3BlbkFJa85rwgVfxghHLIm6CuJz';

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      setMessages([...messages, { type: 'outgoing', text: newMessage }]);
      generateResponse(newMessage);
      setNewMessage('');
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const generateResponse = async (userMessage) => {
    const API_URL = 'https://api.openai.com/v1/chat/completions';
    const requestOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${API_KEY}`,
      },
      body: JSON.stringify({
        model: 'gpt-3.5-turbo',
        messages: [{ role: 'user', content: userMessage }],
      }),
    };

    setMessages((prevMessages) => [
      ...prevMessages,
      { type: 'incoming', text: 'Thinking...' },
    ]);

    try {
      const response = await fetch(API_URL, requestOptions);
      const data = await response.json();
      setMessages((prevMessages) =>
        prevMessages.slice(0, -1).concat({
          type: 'incoming',
          text: data.choices[0].message.content.trim(),
        })
      );
    } catch (error) {
      setMessages((prevMessages) =>
        prevMessages.slice(0, -1).concat({
          type: 'incoming',
          text: 'Oops! Something went wrong. Please try again.',
          error: true,
        })
      );
    }
  };
  return (
    <>
      <button className="chatbot-toggler" onClick={handleToggle}>
        <div className="material-symbols-rounded">
          <i className="fas fa-comment"></i>
        </div>
        <span className="material-symbols-outlined">close</span>
      </button>
      {isOpen && (
        <div className="chatbot">
          <header>
            <h2>Chatbot</h2>
            <span
              className="close-btn material-symbols-outlined"
              onClick={handleToggle}
            >
              close
            </span>
          </header>
          <ul className="chatbox">
            {messages.map((message, index) => (
              <li key={index} className={`chat ${message.type}`}>
                {message.type === 'incoming' && (
                  <span className="material-symbols-outlined">smart_toy</span>
                )}
                <p>{message.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Enter a message..."
              spellCheck="false"
              required
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleKeyDown}
            />
            <span
              id="send-btn"
              className="material-symbols-rounded"
              onClick={handleSendMessage}
            >
              send
            </span>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
