import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import { Helmet } from 'react-helmet-async';
import axios from 'axios';
import { useContext, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import { Store } from '../store';
import { toast } from 'react-toastify';
import { getError } from '../utils';

function ShelterSignupScreen() {
  const { search } = useLocation();
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';
  const [loading, setLoading] = useState(false);
  /*const [formData, setFormData] = useState({
    name: '',
      address: '',
      phone_number: '',
      email: '',
      password: '',
      description: '',
      photos: '',
  }); */
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [phone_number, setPhone_number] = useState('');
  const [description, setDescription] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [agreedToGDPR, setAgreedToGDPR] = useState(false);
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;

  const submitHandler = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      toast.error('Parolele nu sunt la fel');
      return;
    }
    if (!agreedToGDPR) {
      toast.error('Trebuie să fii de acord cu Politica de Confidențialitate');
      return;
    }
    try {
      setLoading(true);

      const { data } = await axios.post('/api/users/shelters/signup', {
        name,
        address,
        phone_number,
        email,
        password,
        description,
      });

      localStorage.setItem('shelters', JSON.stringify(data));
      toast.success(
        'Înregistrare reușită. Un email de confirmare a fost trimis.'
      );
    } catch (err) {
      toast.error(getError(err));
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="small-container">
      <Helmet>
        <title>Sign Up</title>
      </Helmet>
      <h1 className="my-3 be-vietnam-pro-semibold">Înregistrare</h1>
      <Form className="be-vietnam-pro-medium" onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="name">
          <Form.Label>Nume</Form.Label>
          <Form.Control onChange={(e) => setName(e.target.value)} required />
        </Form.Group>

        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Parolă</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
          <Form.Group className="mb-3" controlId="confirmPassword">
            <Form.Label>Confirmă parola</Form.Label>
            <Form.Control
              type="password"
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="address">
            <Form.Label>Adresa:</Form.Label>
            <Form.Control
              type="address"
              onChange={(e) => setAddress(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="phone_number">
            <Form.Label>Numar de telefon:</Form.Label>
            <Form.Control
              type="phone_number"
              onChange={(e) => setPhone_number(e.target.value)}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="description">
            <Form.Label>Descriere:</Form.Label>
            <Form.Control
              type="description"
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </Form.Group>
        </Form.Group>
        <Form.Group controlId="gdprCheckbox" className="mb-3">
          <Form.Check
            type="checkbox"
            label={
              <>
                Sunt de acord cu{' '}
                <a
                  href="/privacy-policy"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Politica de Confidențialitate
                </a>
              </>
            }
            checked={agreedToGDPR}
            onChange={(e) => setAgreedToGDPR(e.target.checked)}
            required
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit" variant="light">
            Înregistare
          </Button>
        </div>
        <div className="mb-3">
          Ai deja un cont?{' '}
          <Link to={`/signin?redirect=${redirect}`}>Autentificare</Link>
        </div>
      </Form>
    </Container>
  );
}

export default ShelterSignupScreen;
