import axios from 'axios';
import { useEffect, useReducer } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, shelter: action.payload, loading: false };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function ShelterInfoScreen(props) {
  const navigate = useNavigate();
  const { params } = useParams();
  const { name } = useParams();
  //const { shelter } = props;
  const [{ loading, error, shelter }, dispatch] = useReducer(reducer, {
    shelter: [],
    loading: true,
    error: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get(`/api/shelters/name/${name}`);
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
      }
    };
    fetchData();
  }, [name]);

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <Col>
      <h1>{shelter.name}</h1>
      <p>{shelter.address}</p>
    </Col>
  );
}
export default ShelterInfoScreen;
