import axios from 'axios';
import { useContext, useEffect, useReducer, useRef, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Row, Col, Button, ListGroup, Badge } from 'react-bootstrap';
import { Helmet } from 'react-helmet-async';
import { Store } from '../store';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return { ...state, pet: action.payload, loading: false };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    case 'USER_FETCH_SUCCESS':
      return { ...state, user: action.payload };
    default:
      return state;
  }
};

function PetScreen() {
  const navigate = useNavigate();

  const params = useParams();
  const { slug } = params;
  const [{ loading, error, pet }, dispatch] = useReducer(reducer, {
    pet: [],
    loading: true,
    error: '',
  });

  // Index of the currently displayed photo
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get(`/api/pets/slug/${slug}`);
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
        dispatch({ type: 'USER_FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
      }
    };
    fetchData();
  }, [slug]);

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { favorites } = state;
  const addToFavoritesHandler = () => {
    const existItem = favorites.favoritesItems.find((x) => x._id === pet._id);
    if (!existItem) {
      ctxDispatch({ type: 'FAVORITES_ADD_ITEM', payload: pet });
    } else {
      window.alert('Ai adaugat deja acest animal la favorite');
    }
  };

  const adoptHandler = () => {
    navigate('/signin?redirect=/form');
  };

  // Function to handle next photo
  const nextPhoto = () => {
    setCurrentPhotoIndex((prevIndex) =>
      prevIndex === pet.photos.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Function to handle previous photo
  const prevPhoto = () => {
    setCurrentPhotoIndex((prevIndex) =>
      prevIndex === 0 ? pet.photos.length - 1 : prevIndex - 1
    );
  };

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <div>
      <Row className="d-flex justify-content-around">
        <Col className="secondary-img" md={3}></Col>
        <Col md={6}>
          <img
            className="img-fluid img-large"
            src={pet.photos[currentPhotoIndex]}
            alt={pet.name}
          ></img>
          <div>
            <Button onClick={prevPhoto}>{'<'}</Button>
            <Button onClick={nextPhoto}>{'>'}</Button>
          </div>
        </Col>

        <Col className="petscreen-info be-vietnam-pro-medium" md={3} sm={6}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <Helmet>
                <title>{pet.name}</title>
              </Helmet>
              <div className="d-flex flex-row justify-content-between">
                <h1>{pet.name}</h1>
                <div className="adopt-button ">
                  <Button onClick={addToFavoritesHandler} variant="dark">
                    <i className="fas fa-heart"></i>
                  </Button>
                </div>
              </div>
            </ListGroup.Item>
            {/* Rest of the list group items */}
          </ListGroup>
        </Col>

        <Col md={3}></Col>
      </Row>
    </div>
  );
}

export default PetScreen;
