import axios, { Axios } from 'axios';
import { useContext, useReducer, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/esm/Container';
import Form from 'react-bootstrap/Form';
import { Helmet } from 'react-helmet-async';
import { useLocation, useNavigate } from 'react-router-dom';
import { Store } from '../store';
import { toast } from 'react-toastify';
import { getError } from '../utils';

export default function AddPostScreen() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [breed, setBreed] = useState('');
  const [gender, setGender] = useState('');
  const [address, setAddress] = useState('');
  const [description, setDescription] = useState('');
  const [medicalInfo, setMedicalInfo] = useState('');
  const [adoptionStatus, setAdoptionStatus] = useState('Disponibil');
  const [photos, setPhotos] = useState([]);

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { pets } = state;

  const navigate = useNavigate();
  const { search } = useLocation();

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await Axios.post('/api/users/addpost', {
        name,
        age,
        breed,
        gender,
        address,
        description,
        medicalInfo,
        adoptionStatus,
        photos,
      });
      ctxDispatch({ type: 'PET_ADD_ITEM', payload: data });
      localStorage.setItem('userInfo', JSON.stringify(data));
      navigate('/');
    } catch (err) {
      toast.error(getError(err));
    }
  };

  return (
    <Container className="small-container">
      <Helmet>
        <title>Adauga anunt</title>
      </Helmet>
      <h1 className="my-30 be-vietnam-pro-semibold">Adauga anunt</h1>
      <Form className="be-vietnam-pro-medium" onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="name">
          <Form.Label>Numele animalutului</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="age">
          <Form.Label>Varsta</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setAge(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="breed">
          <Form.Label>Rasa</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setBreed(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="gender">
          <Form.Label>Genul</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setGender(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="address">
          <Form.Label>Adresa</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setAddress(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="description">
          <Form.Label>Descriere</Form.Label>
          <Form.Control
            type="text"
            required
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="photos">
          <Form.Label>Poze</Form.Label>
          <Form.Control
            type="photos"
            required
            onChange={(e) => setAge(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="medicalInfo">
          <Form.Label>Informatii medicale</Form.Label>
          <Form.Control
            type="medicalInfo"
            required
            onChange={(e) => setMedicalInfo(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Salveaza</Button>
        </div>
      </Form>
    </Container>
  );
}
