import React, { useState, useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import {
  GoogleMap,
  Marker,
  InfoWindow,
  useJsApiLoader,
} from '@react-google-maps/api';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Form from 'react-bootstrap/Form';

const containerStyle = {
  display: 'flex',
  width: '800px',
  height: '500px',
  marginTop: '20px',
  marginRight: '20px',
  marginBottom: '30px',
  marginLeft: '150px',
};

const center = {
  lat: 45.759,
  lng: 21.2197,
};

function MyComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: 'YOUR_GOOGLE_MAPS_API_KEY',
  });

  const navigate = useNavigate();
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [shelters, setShelters] = useState([]);
  const [animalPosts, setAnimalPosts] = useState([]);
  const [filter, setFilter] = useState('animalPosts'); // Default filter
  const [selectedMarker, setSelectedMarker] = useState(null);

  const filters = [
    { name: 'Adaposturi', value: 'shelters' },
    { name: 'Anunturi de animale', value: 'animalPosts' },
  ];

  useEffect(() => {
    const fetchShelters = async () => {
      try {
        const response = await axios.get('/api/shelters');
        setShelters(response.data);
      } catch (error) {
        console.error('Error fetching shelters:', error);
      }
    };

    const fetchAnimalPosts = async () => {
      try {
        const response = await axios.get('/api/pets');
        setAnimalPosts(response.data);
      } catch (error) {
        console.error('Error fetching animal posts:', error);
      }
    };

    fetchShelters();
    fetchAnimalPosts();
  }, []);

  useEffect(() => {
    if (isLoaded) {
      const geocoder = new window.google.maps.Geocoder();
      const newMarkers = [];

      const itemsToMap = filter === 'shelters' ? shelters : animalPosts;

      itemsToMap.forEach((item, index) => {
        const address = item.address;
        geocoder.geocode({ address: address }, (results, status) => {
          if (status === 'OK') {
            const location = results[0].geometry.location;
            newMarkers.push({
              position: { lat: location.lat(), lng: location.lng() },
              title: item.name,
              id: index,
              address: address,
            });
            if (index === itemsToMap.length - 1) {
              setMarkers(newMarkers);
            }
          } else {
            console.error(
              `Geocode was not successful for the following reason: ${status}`
            );
          }
        });
      });
    }
  }, [isLoaded, filter, shelters, animalPosts]);

  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);
    setMap(map);
  }, []);

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null);
  }, []);

  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  return isLoaded ? (
    <>
      <Form.Select
        aria-label="Default select example"
        style={{ marginBottom: '20px' }}
        value={filter}
        onChange={handleFilterChange}
      >
        {filters.map((filterOption) => (
          <option key={filterOption.value} value={filterOption.value}>
            {filterOption.name}
          </option>
        ))}
      </Form.Select>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
        onLoad={onLoad}
        onUnmount={onUnmount}
      >
        {markers.map((marker, index) => (
          <Marker
            key={index}
            position={marker.position}
            title={marker.title}
            onClick={() => setSelectedMarker(marker)}
          />
        ))}
        {selectedMarker && (
          <InfoWindow
            position={selectedMarker.position}
            onCloseClick={() => setSelectedMarker(null)}
          >
            <div>
              <h4>{selectedMarker.title}</h4>
              <p>{selectedMarker.address}</p>
              <Link to={`/shelter/${selectedMarker.title}`}>Details</Link>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </>
  ) : (
    <></>
  );
}

export default React.memo(MyComponent);
