import React, { useState, useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';

const containerStyle = {
  display: 'flex',
  width: '800px',
  height: '500px',
  marginTop: '20px',
  marginRight: '20px',
  marginBottom: '30px',
  marginLeft: '150px',
};

const center = {
  lat: 45.759,
  lng: 21.2197,
};

function MyComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: 'AIzaSyBqSxES0gUd7JoNggJUjnRh5bDS2JXjUZ4',
  });

  const navigate = useNavigate();
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [shelters, setShelters] = useState([]);
  const [animalPosts, setAnimalPosts] = useState([]);
  const [filter, setFilter] = useState('shelters'); // Default filter

  const filters = [
    { name: 'Shelters', value: 'shelters' },
    { name: 'Animal Posts', value: 'animalPosts' },
  ];

  useEffect(() => {
    const fetchShelters = async () => {
      try {
        const response = await axios.get('/api/shelters');
        setShelters(response.data);
      } catch (error) {
        console.error('Error fetching shelters:', error);
      }
    };

    const fetchAnimalPosts = async () => {
      try {
        const response = await axios.get('/api/animals');
        setAnimalPosts(response.data);
      } catch (error) {
        console.error('Error fetching animal posts:', error);
      }
    };

    fetchShelters();
    fetchAnimalPosts();
  }, []);

  useEffect(() => {
    if (isLoaded) {
      const geocoder = new window.google.maps.Geocoder();
      const itemsToMap = filter === 'shelters' ? shelters : animalPosts;

      if (itemsToMap.length > 0) {
        const newMarkers = [];

        itemsToMap.forEach((item, index) => {
          const address = item.address;
          geocoder.geocode({ address: address }, (results, status) => {
            if (status === 'OK') {
              const location = results[0].geometry.location;
              newMarkers.push({
                position: { lat: location.lat(), lng: location.lng() },
                title: item.name,
                id: index,
                type: filter, // Adding type to the marker
                slug: item.slug, // Assuming each item has a slug for navigation
              });
              if (index === itemsToMap.length - 1) {
                setMarkers(newMarkers);
              }
            } else {
              console.error(
                `Geocode was not successful for the following reason: ${status}`
              );
            }
          });
        });
      } else {
        setMarkers([]);
      }
    }
  }, [isLoaded, filter, shelters, animalPosts]);

  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);

    setMap(map);
  }, []);

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null);
  }, []);

  const handleMarkerClick = (marker) => {
    if (marker.type === 'shelters') {
      navigate(`/shelter/${encodeURIComponent(marker.slug)}`);
    } else if (marker.type === 'animalPosts') {
      navigate(`/pet/${encodeURIComponent(marker.slug)}`);
    }
  };

  const customMarkerIcon = isLoaded
    ? {
        url: '/images/Paw.png',
        scaledSize: new window.google.maps.Size(60, 50),
        origin: new window.google.maps.Point(0, 0),
        anchor: new window.google.maps.Point(20, 20),
      }
    : null;

  return isLoaded ? (
    <Col>
      <h1 className="mb-5">HartÄƒ</h1>

      <ButtonGroup className="mb-3">
        {filters.map((filterOption, idx) => (
          <ToggleButton
            key={idx}
            type="radio"
            variant="secondary"
            name="filter"
            value={filterOption.value}
            checked={filter === filterOption.value}
            onChange={(e) => setFilter(e.currentTarget.value)}
          >
            {filterOption.name}
          </ToggleButton>
        ))}
      </ButtonGroup>

      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
        onLoad={onLoad}
        onUnmount={onUnmount}
      >
        {markers.map((marker, index) => (
          <Marker
            key={index}
            position={marker.position}
            icon={customMarkerIcon}
            onClick={() => handleMarkerClick(marker)}
          />
        ))}
      </GoogleMap>
    </Col>
  ) : (
    <></>
  );
}

export default React.memo(MyComponent);
