import { Link } from 'react-router-dom';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { useContext } from 'react';
import { Store } from '../store';

function Pet(props) {
  const { pet } = props;
  const { state, dispatch: ctxDispatch } = useContext(Stoore);
  const { favorites } = state;
  const addToFavoritesHandler = () => {
    const existItem = favorites.favoritesItems.find((x) => x._id === pet._id);
    if (!existItem) {
      ctxDispatch({ type: 'FAVORITES_ADD_ITEM', payload: pet });
    } else {
      window.alert('Ai adaugat deja acest animal la favorite');
    }
    //navigate('/favorites');
  };
  return (
    <Card className="pet-card">
      <Link to={`/pet/${pet.slug}`}>
        <img src={pet.photos[0]} className="card-img-top" alt={pet.name} />
      </Link>
      <div className="pet-info">
        <Link className="name-link" to={`/pet/${pet.slug}`}>
          <p>
            <strong>{pet.name}</strong>
          </p>
        </Link>

        <div className="pet-tags">
          <p>{pet.age}</p>
          <p>{pet.breed}</p>
          <p>{pet.gender}</p>
        </div>
        <p>{pet.address}</p>
        <div className="adopt-button ">
          <Button onClick={addToFavoritesHandler} variant="dark">
            <i className="fas fa-heart"></i>
          </Button>
        </div>
      </div>
    </Card>
  );
}
export default Pet;
