import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'incoming', text: 'Hi there ðŸ‘‹\nHow can I help you today?' },
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSendMessage = async () => {
    try {
      const response = await axios.post('/api/chatbot/compare', {
        question: newMessage, // Trimite Ã®ntrebarea cÄƒtre backend
      });

      const responseData = response.data;
      console.log('Response:', responseData);

      // PoÈ›i actualiza mesajele din state Ã®n funcÈ›ie de rÄƒspunsul primit de la backend
      setMessages((prevMessages) => [
        ...prevMessages,
        { type: 'incoming', text: responseData.response },
      ]);
    } catch (error) {
      console.error('Error:', error);
      // PoÈ›i afiÈ™a un mesaj de eroare Ã®n cazul Ã®n care apare o problemÄƒ Ã®n comunicarea cu backend-ul
      setMessages((prevMessages) => [
        ...prevMessages,
        {
          type: 'incoming',
          text: 'Oops! Something went wrong. Please try again.',
        },
      ]);
    }
  };
  const handleGetAnswers = async (questionId) => {
    try {
      const response = await axios.get(
        `/api/chatbot/questions/${questionId}/answers`
      );
      const answers = response.data;

      console.log('Answers:', answers);
      setNewMessage(answers);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    // Aici poÈ›i Ã®nlocui 'questionId' cu ID-ul real al Ã®ntrebÄƒrii
    handleGetAnswers(1);
  }, []);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <>
      <button className="chatbot-toggler" onClick={handleToggle}>
        <div className="material-symbols-rounded">
          <i className="fas fa-comment"></i>
        </div>
        <span className="material-symbols-outlined">close</span>
      </button>
      {isOpen && (
        <div className="chatbot">
          <header>
            <h2>Chatbot</h2>
            <span
              className="close-btn material-symbols-outlined"
              onClick={handleToggle}
            >
              close
            </span>
          </header>
          <ul className="chatbox">
            {messages.map((message, index) => (
              <li key={index} className={`chat ${message.type}`}>
                {message.type === 'incoming' && (
                  <span className="material-symbols-outlined">smart_toy</span>
                )}
                <p>{message.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Enter a message..."
              spellCheck="false"
              required
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyDown={handleKeyDown}
            />
            <span
              id="send-btn"
              className="material-symbols-rounded"
              onClick={handleSendMessage}
            >
              send
            </span>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
