import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'incoming', text: 'Hi there ðŸ‘‹\nHow can I help you today?' },
  ]);
  const [newAnswer, setNewAnswer] = useState('');
  const [questionId, setQuestionId] = useState(''); // StocÄƒm ID-ul Ã®ntrebÄƒrii la care vrem sÄƒ adÄƒugÄƒm rÄƒspunsul

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSendMessage = async () => {
    if (newAnswer.trim() && questionId.trim()) {
      try {
        const response = await axios.post(
          `/api/chatbot/questions/${questionId}/answers`,
          {
            text: newAnswer,
          }
        );
        if (response.status === 201) {
          setMessages([...messages, { type: 'outgoing', text: newAnswer }]);
        }
      } catch (error) {
        console.error('Error:', error);
        setMessages([
          ...messages,
          {
            type: 'incoming',
            text: 'Oops! Something went wrong. Please try again.',
          },
        ]);
      }
      setNewAnswer('');
    }
  };

  return (
    <>
      <button className="chatbot-toggler" onClick={handleToggle}>
        <div className="material-symbols-rounded">
          <i className="fas fa-comment"></i>
        </div>
        <span className="material-symbols-outlined">close</span>
      </button>
      {isOpen && (
        <div className="chatbot">
          <header>
            <h2>Chatbot</h2>
            <span
              className="close-btn material-symbols-outlined"
              onClick={handleToggle}
            >
              close
            </span>
          </header>
          <ul className="chatbox">
            {messages.map((message, index) => (
              <li key={index} className={`chat ${message.type}`}>
                {message.type === 'incoming' && (
                  <span className="material-symbols-outlined">smart_toy</span>
                )}
                <p>{message.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Enter a response..."
              spellCheck="false"
              required
              value={newAnswer}
              onChange={(e) => setNewAnswer(e.target.value)}
            />
            <input
              type="text"
              placeholder="Question ID"
              value={questionId}
              onChange={(e) => setQuestionId(e.target.value)}
            />
            <span
              id="send-btn"
              className="material-symbols-rounded"
              onClick={handleSendMessage}
            >
              send
            </span>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
