import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'incoming', text: 'Hi there ðŸ‘‹\nHow can I help you today?' },
  ]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false); // AdÄƒugÄƒm o stare pentru a afiÈ™a Ã®ncÄƒrcarea

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSendMessage = async () => {
    try {
      setMessages([...messages, { type: 'outgoing', text: newMessage }]); // AdÄƒugÄƒm Ã®ntrebarea Ã®n lista de mesaje

      setIsLoading(true); // ActivÄƒm starea de Ã®ncÄƒrcare

      // Trimitem Ã®ntrebarea cÄƒtre server
      const response = await axios.post('/api/chatbot/questions/compare', {
        text: newMessage,
      });

      const similarQuestions = response.data;
      if (similarQuestions.length > 0) {
        // DacÄƒ existÄƒ Ã®ntrebÄƒri similare, alegem prima Ã®ntrebare È™i afiÈ™Äƒm rÄƒspunsul corespunzÄƒtor
        const questionId = similarQuestions[0]._id;
        const answerResponse = await axios.get(
          `/api/chatbot/questions/${questionId}/answers`
        );
        const answers = answerResponse.data;
        setMessages([
          ...messages,
          ...answers.map((answer) => ({ type: 'incoming', text: answer.text })),
        ]);
      } else {
        // DacÄƒ nu existÄƒ Ã®ntrebÄƒri similare, afiÈ™Äƒm un mesaj corespunzÄƒtor
        setMessages([
          ...messages,
          {
            type: 'incoming',
            text: "I'm sorry, I couldn't find an answer to your question.",
          },
        ]);
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false); // DezactivÄƒm starea de Ã®ncÄƒrcare
    }

    // ResetÄƒm Ã®ntrebarea
    setNewMessage('');
  };

  return (
    <>
      <button className="chatbot-toggler" onClick={handleToggle}>
        <div className="material-symbols-rounded">
          <i className="fas fa-comment"></i>
        </div>
        <span className="material-symbols-outlined">close</span>
      </button>
      {isOpen && (
        <div className="chatbot">
          <header>
            <h2>Chatbot</h2>
            <span
              className="close-btn material-symbols-outlined"
              onClick={handleToggle}
            >
              close
            </span>
          </header>
          <ul className="chatbox">
            {messages.map((message, index) => (
              <li key={index} className={`chat ${message.type}`}>
                {message.type === 'incoming' && (
                  <span className="material-symbols-outlined">smart_toy</span>
                )}
                <p>
                  {message.type === 'outgoing' && '...'} {message.text}
                </p>{' '}
                {/* AfiÈ™Äƒm punctele de suspensie pentru mesajele trimise */}
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Enter a question..."
              spellCheck="false"
              required
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <span
              id="send-btn"
              className="material-symbols-rounded"
              onClick={handleSendMessage}
            >
              send
            </span>
            {isLoading && <p>Loading...</p>}{' '}
            {/* AfiÈ™Äƒm "Loading..." Ã®n timpul Ã®ncÄƒrcÄƒrii */}
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
