import { Button } from 'bootstrap';
import MessageBox from '../components/MessageBox';
import { toast } from 'react-toastify';
import { getError } from '../utils';
import axios from 'axios';
import { useContext, useEffect, useReducer } from 'react';
import { useNavigate } from 'react-router-dom';
import { Store } from '../store';

const ProfileScreen = () => {
  const [{ loading, error, pets, successDelete }, dispatch] = useReducer(
    reducer,
    {
      pets: [],
      loading: true,
      error: '',
    }
  );

  const { state } = useContext(Store);
  const { userInfo } = state;
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const { data } = await axios.get('/api/pets/user', {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });
        dispatch({ type: 'FETCH_SUCCESS', payload: data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: getError(err) });
      }
    };
    if (successDelete) {
      dispatch({ type: 'DELETE_RESET' });
      fetchData();
    } else {
      fetchData();
    }
  }, [userInfo, successDelete]);

  const deleteHandler = async (petId) => {
    if (window.confirm('Are you sure to delete?')) {
      try {
        await axios.delete(`/api/pets/${petId}`, {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        });
        dispatch({ type: 'DELETE_SUCCESS' });
        toast.success('Pet deleted successfully');
      } catch (err) {
        dispatch({ type: 'DELETE_FAIL' });
        toast.error(getError(err));
      }
    }
  };

  const changeAdoptionStatusHandler = async (petId, newStatus) => {
    try {
      await axios.put(
        `/api/pets/${petId}`,
        { adoption_status: newStatus },
        {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        }
      );
      toast.success('Adoption status updated successfully');
      // Re-fetch the pets after status update
      fetchData();
    } catch (err) {
      toast.error(getError(err));
    }
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <>
          <h2>Animăluțele mele</h2>
          {pets.map((pet) => (
            <div key={pet._id} className="mb-3">
              <h3>{pet.name}</h3>
              <p>{pet.description}</p>
              <img src={pet.photo} alt={pet.name} />
              <Button variant="danger" onClick={() => deleteHandler(pet._id)}>
                <i className="fas fa-trash"></i> Delete
              </Button>
              <Button
                variant="primary"
                onClick={() =>
                  changeAdoptionStatusHandler(pet._id, !pet.adoption_status)
                }
              >
                {pet.adoption_status
                  ? 'Mark as Not Adopted'
                  : 'Mark as Adopted'}
              </Button>
            </div>
          ))}
        </>
      )}
    </div>
  );
};

export default ProfileScreen;
