import React, { useState, useEffect } from 'react';
import {
  GoogleMap,
  Marker,
  InfoWindow,
  useJsApiLoader,
} from '@react-google-maps/api';
import { useNavigate } from 'react-router-dom';

const containerStyle = {
  display: 'flex',
  width: '600px',
  height: '500px',
};

const center = {
  lat: 45.66667,
  lng: 21.5,
};

const addresses = [
  { address: 'Comuna Şag, DN 59, Km 12+200', info: 'Adăpost animale 1' },
  {
    address: 'Comuna Sacoşu Turcesc, sat Otveşti, strada Principală nr. 217',
    info: 'Adăpost animale 2',
  },
  { address: 'Sat Tapia, nr. 10', info: 'Adăpost animale 3' },
];

function MyComponent() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: 'AIzaSyBqSxES0gUd7JoNggJUjnRh5bDS2JXjUZ4',
  });

  const navigate = useNavigate();
  const [map, setMap] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);

  useEffect(() => {
    if (isLoaded) {
      const geocoder = new window.google.maps.Geocoder();
      const newMarkers = [];

      addresses.forEach((place, index) => {
        geocoder.geocode({ address: place.address }, (results, status) => {
          if (status === 'OK') {
            const location = results[0].geometry.location;
            newMarkers.push({
              position: { lat: location.lat(), lng: location.lng() },
              title: place.info,
              id: index,
            });
            setMarkers([...newMarkers]);
          } else {
            console.error(
              `Geocode was not successful for the following reason: ${status}`
            );
          }
        });
      });
    }
  }, [isLoaded]);

  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);

    setMap(map);
  }, []);

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null);
  }, []);

  const handleMarkerClick = (marker) => {
    setSelectedMarker(marker);
    navigate(`/shelter/${encodeURIComponent(shelterName)}`);
  };

  const handleInfoWindowClose = () => {
    setSelectedMarker(null);
  };

  return isLoaded ? (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={10}
      onLoad={onLoad}
      onUnmount={onUnmount}
    >
      {markers.map((marker) => (
        <Marker
          key={marker.id}
          position={marker.position}
          title={marker.title}
          onClick={() => handleMarkerClick(marker)}
        />
      ))}
      {selectedMarker && (
        <InfoWindow
          position={selectedMarker.position}
          onCloseClick={handleInfoWindowClose}
        >
          <div>
            <h3>{selectedMarker.title}</h3>
            <p>Additional information can be added here.</p>
          </div>
        </InfoWindow>
      )}
    </GoogleMap>
  ) : (
    <></>
  );
}

export default React.memo(MyComponent);
