import axios from 'axios';
import { useContext, useEffect, useReducer } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import MessageBox from '../components/MessageBox';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Rating from '../components/Rating';
import { Store } from '../store';
import { toast } from 'react-toastify';
import Button from 'react-bootstrap/Button';
import { getError } from '../utils';
import Pet from '../components/Pet';

const reducer = (state, action) => {
  switch (action.type) {
    case 'REFRESH_SHELTER':
      return { ...state, shelter: action.payload };
    case 'CREATE_REQUEST':
      return { ...state, loadingCreateReview: true };
    case 'CREATE_SUCCESS':
      return { ...state, loadingCreateReview: false };
    case 'CREATE_FAIL':
      return { ...state, loadingCreateReview: false };
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    case 'FETCH_SUCCESS':
      return {
        ...state,
        shelter: action.payload.shelter,
        pets: action.payload.pets,
        loading: false,
      };
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    case 'PETS_FETCH_REQUEST':
      return { ...state, loadingPets: true };
    case 'PETS_FETCH_SUCCESS':
      return { ...state, pets: action.payload, loadingPets: false };
    case 'PETS_FETCH_FAIL':
      return { ...state, loadingPets: false, errorPets: action.payload };
    default:
      return state;
  }
};

function ShelterInfoScreen(props) {
  const { name } = useParams();
  const [{ loading, error, shelter, pets }, dispatch] = useReducer(reducer, {
    shelter: {},
    pets: [],
    loading: true,
    error: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get(`/api/shelters/name/${name}`);
        dispatch({
          type: 'FETCH_SUCCESS',
          payload: { shelter: result.data, pets: result.data.pets },
        });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message });
      }
    };
    fetchData();
  }, [name]);

  useEffect(() => {
    const fetchPets = async () => {
      dispatch({ type: 'PETS_FETCH_REQUEST' });
      try {
        const result = await axios.get('/api/pets');
        dispatch({ type: 'PETS_FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'PETS_FETCH_FAIL', payload: getError(err) });
      }
    };
    fetchPets();
  }, []);

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <MessageBox variant="danger">{error}</MessageBox>
  ) : (
    <Container>
      <Row>
        <Col>
          <h1>{shelter.name}</h1>
          <Rating rating={shelter.rating} />
          <p>{shelter.address}</p>
          <p>{shelter.phone_number}</p>
          <p>{shelter.email}</p>
          <p>{shelter.description}</p>
        </Col>
        <Col>
          <Row>
            {shelter.photos.map((photo, index) => (
              <Col key={index} xs={6} md={4}>
                <Card>
                  <Card.Img variant="top" src={photo} />
                </Card>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
      <Row>
        <Col>
          <h2>Animale disponibile</h2>
          <Row>
            {pets.map((pet) => (
              <Col key={pet._id} sm={6} md={6} lg={3} className="mb-3">
                <Pet pet={pet}></Pet>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

export default ShelterInfoScreen;
