import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { type: 'incoming', text: 'Hi there 游녦\nHow can I help you today?' },
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleSendMessage = async () => {
    try {
      const response = await axios.post('/api/chatbot/questions', {
        text: newMessage,
        category: 'default',
      });
      const { _id } = response.data;
      // Apel캒m func탵ia pentru ob탵inerea r캒spunsului doar dac캒 primim un ID valid pentru 칥ntrebare
      if (_id) {
        getAnswers(_id);
      }
    } catch (error) {
      console.error('Error:', error);
    }
    setNewMessage('');
  };

  const getAnswers = async (questionId) => {
    try {
      const response = await axios.get(
        `/api/chatbot/questions/${questionId}/answers`
      );
      const answers = response.data;
      // Ad캒ug캒m r캒spunsurile la mesaje
      setMessages([
        ...messages,
        ...answers.map((answer) => ({ type: 'incoming', text: answer.text })),
      ]);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    // Nimic nu se va 칥nt칙mpla c칙nd componenta se monteaz캒 ini탵ial
    // Ne vom baza pe apelul func탵iei de trimitere a mesajului pentru a ini탵ia procesul
  }, []);

  return (
    <>
      <button className="chatbot-toggler" onClick={handleToggle}>
        <div className="material-symbols-rounded">
          <i className="fas fa-comment"></i>
        </div>
        <span className="material-symbols-outlined">close</span>
      </button>
      {isOpen && (
        <div className="chatbot">
          <header>
            <h2>Chatbot</h2>
            <span
              className="close-btn material-symbols-outlined"
              onClick={handleToggle}
            >
              close
            </span>
          </header>
          <ul className="chatbox">
            {messages.map((message, index) => (
              <li key={index} className={`chat ${message.type}`}>
                {message.type === 'incoming' && (
                  <span className="material-symbols-outlined">smart_toy</span>
                )}
                <p>{message.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Enter a question..."
              spellCheck="false"
              required
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <span
              id="send-btn"
              className="material-symbols-rounded"
              onClick={handleSendMessage}
            >
              send
            </span>
          </div>
        </div>
      )}
    </>
  );
};

export default Chatbot;
