import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ListGroup from 'react-bootstrap/ListGroup';
import Pet from '../components/Pet';
import MessageBox from '../components/MessageBox';
import Rating from '../components/Rating';

function ShelterInfoScreen() {
  const { name } = useParams();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [shelter, setShelter] = useState({});
  const [animalPosts, setAnimalPosts] = useState([]);

  useEffect(() => {
    const fetchShelterInfo = async () => {
      try {
        const { data: fetchedShelter } = await axios.get(`/api/shelters/${id}`);
        const { data: fetchedAnimalPosts } = await axios.get(
          `/api/shelters/${name}/posts`
        );
        setShelter(fetchedShelter);
        setAnimalPosts(fetchedAnimalPosts);
        setLoading(false);
      } catch (error) {
        setError('Eroare în obținerea informațiilor despre adăpost');
        setLoading(false);
      }
    };

    fetchShelterInfo();
  }, [id]);

  return (
    <Container>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <>
          <Row>
            <Col>
              <h1>{shelter.name}</h1>
              <Rating rating={shelter.rating} />
              <p>{shelter.address}</p>
              <p>{shelter.phone_number}</p>
              <p>{shelter.email}</p>
              <p>{shelter.description}</p>
            </Col>
          </Row>
          <Row>
            <Col>
              <h2>Animale disponibile</h2>
              {animalPosts.length === 0 ? (
                <MessageBox>
                  Nu există anunțuri de animale disponibile.
                </MessageBox>
              ) : (
                <ListGroup>
                  {animalPosts.map((post) => (
                    <ListGroup.Item key={post._id}>
                      <Pet pet={post} />
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              )}
            </Col>
          </Row>
        </>
      )}
    </Container>
  );
}

export default ShelterInfoScreen;
